INSERT INTO `bm_secrecy_scope` VALUES ('8bf01a81657d42c6b59be0c876d66d6e', '烟草行业工作中国家秘密及其密级具体范围的规定', '国家保密局文件国烟办[1996]第22号', null, '<p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">烟草行业工作中国家秘密</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">及其密级具体范围的规定</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;（<font face=\"Times New Roman\">1996</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">29</font><font face=\"宋体\">日）</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第一条&nbsp;&nbsp;根据《中华人民共和国保守国家秘密法》和《中华人民共和国保守国家秘密法实施办法》的规定，为维护国家的安全和利益，促进烟草行业的发展，制定本规定。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第二条&nbsp;&nbsp;烟草行业工作中的国家秘密是指关系国家的安全和利益，依照法定程序确定，在一定时间内只限一定范围的人员知悉的事项。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第三条&nbsp;&nbsp;烟草行业工作中国家秘密及其密级的具体范围：</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;（一）机密级事项</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;1.<font face=\"宋体\">代表国家对外谈判中内部掌握的价格和标底，进口配额，国别和客户政策；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;2.<font face=\"宋体\">通过特殊渠道获取的技术资料、样品及其来源。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第四条&nbsp;&nbsp;烟草行业工作中涉及其他部门的国家秘密事项，其密级按国家有关主管部门的保密范围确定。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第五条&nbsp;&nbsp;本规定由烟草专卖局负责解释。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第六条&nbsp;&nbsp;本规定自<font face=\"Times New Roman\">1996</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">6</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">6</font><font face=\"宋体\">日起实施。</font><font face=\"Times New Roman\">1989</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">8</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">14</font><font face=\"宋体\">日由国家烟草专卖局、国家保密局联合颁发的《烟草行业中国家秘密及其密级具体范围的规定》（</font><font face=\"Times New Roman\">[1989]</font><font face=\"宋体\">国烟办第</font><font face=\"Times New Roman\">13</font><font face=\"宋体\">号）同时废止。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 189pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&mdash;&mdash;摘自<font face=\"Times New Roman\">1996</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">29</font><font face=\"宋体\">日国家烟草专卖局</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国家保密局文件国烟办<font face=\"Times New Roman\">[1996]</font><font face=\"宋体\">第</font><font face=\"Times New Roman\">22</font><font face=\"宋体\">号</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><!--EndFragment-->', '国家烟草专卖局', '全国', '4', '6', '6', '1', '1996-04-29 00:00:00', null, 'ca82ca4a32fc15d50132fc2ade36000e', 'ca82ca4a32fc15d50132fc2ade26000d', 'ca82ca4a32fc15d50132fc3293f70010', '2011-10-25 00:00:00', null, null, null, null, '0');
INSERT INTO `bm_secrecy_scope` VALUES ('97ffa425b7c34c7493c40017e69842e8', '关于调整《海洋工作中国家秘密及其密级具体范围的规定》有关条款的通知', '国海密字[1997]第133号', null, '<p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-ALIGN: center\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">关于调整《海洋工作中国家秘密及其</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">密级具体范围的规定》有关条款的通知</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-ALIGN: center\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">（<font face=\"Times New Roman\">1997</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">3</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">28</font><font face=\"宋体\">日）</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;根据《中华人民共和国保守国家秘密法》第十条规定，国家海洋局、国家保密局修订印发了《海洋工作中国家秘密及其密级具体范围的规定》（国海密字<font face=\"Times New Roman\">[1996]</font><font face=\"宋体\">第</font><font face=\"Times New Roman\">450</font><font face=\"宋体\">号）。现对该&ldquo;规定&rdquo;中笫三条第二款第</font><font face=\"Times New Roman\">7</font><font face=\"宋体\">日&ldquo;海洋系统通信使用的密码、密语&rdquo;（机密级），调整为&ldquo;海洋系统通信使用的密语。&rdquo;海洋系统通信使用的密码的密级，按有关业务主管部门的保密范围规定确定为绝密级。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&mdash;&mdash;摘自<font face=\"Times New Roman\">1997</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">3</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">28</font><font face=\"宋体\">日国家海洋局、国家保密局文件</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国海密字<font face=\"Times New Roman\">[1997]</font><font face=\"宋体\">第</font><font face=\"Times New Roman\">133</font><font face=\"宋体\">号</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><!--EndFragment-->', '国家海洋局、国家保密局', '全国', '4', '7', '6', '1', '1997-03-28 00:00:00', null, 'ca82ca4a32fc15d50132fc2ade36000e', 'ca82ca4a32fc15d50132fc2ade26000d', 'ca82ca4a32fc15d50132fc3293f70010', '2011-10-25 00:00:00', null, null, null, null, '0');
INSERT INTO `bm_secrecy_scope` VALUES ('b3aaccdb7a554b759dcc3143ced29b58', '纺织工业国家秘密及其密级具体范围的规定（1996年4月8日）', '纺办[1996]13号', null, '<div class=\"Section0\" style=\"LAYOUT-GRID:  15.6pt none\"><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\" align=\"center\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">纺织工业国家秘密及其密级具体范围的规定（<font face=\"Times New Roman\">1996</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">8</font><font face=\"宋体\">日）</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第一条&nbsp;根据《中华人民共和国保守国家秘密法》和《中华人民共和国保守国家秘密法实施办法》的规定，为维护国家的安全和利益，保障纺织工业生产建设的顺利进行，特制定本规定。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第二条&nbsp;纺织工业国家秘密是指关系国家的安全和利益，经过法定程序确定，在一定时间内只限一定范围的人员知悉的事项。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;第三条&nbsp;纺织工业国家秘密及其密级的具体范围：</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;（一）机密级事项</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;1<font face=\"宋体\">尚未公布的全国纺织工业重大改革实施方案；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;2<font face=\"宋体\">尚未公布的全国纺织工业（包括各个分行业）中、长期发展计划、规划中的主要指标数字和政策、措施：</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;3<font face=\"宋体\">制订全国纺织工业发展计划、规划过程中的预测资料和重要的纺织经济分析情况；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;4<font face=\"宋体\">为国防军工、重大保密专项工程配套的纺织品数量、性能、标准、用途；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;5<font face=\"宋体\">军需、军工项目专用材料和纺织产品的科研任务书、试验研制及其重要资料；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;6<font face=\"宋体\">全国纺织工业引进技术项目及设备进口计划、用汇额及有关资料；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">7<font face=\"宋体\">国家项目的工程招标、技贸结合项目的招标、投标的重要信息；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">8<font face=\"宋体\">消化、吸收引进国外重大技术及设备过程中，经我方重大改进创新的内容及资料；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">9<font face=\"宋体\">通过特殊方式获取的先进的纺织技术、工艺、配方及设备来源。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">（二）秘密级事项</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">1<font face=\"宋体\">尚未公布的省、自治区、直辖市纺织工业中、长期发展计划、规划中的主要指标数字及实现的政策、措施；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">2<font face=\"宋体\">尚未公布的纺织行业各专业计划、规划中的关键数字和重要政策、措施；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">3<font face=\"宋体\">国家利用外资引进技术和设备的大中型建设项目可行性研究报告中的投资、综合经济评价内容和设计文件：</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">4<font face=\"宋体\">尚未公布的全国纺织行业利用外资计划、设备技术引进计划中的使用外汇额度，批准用汇额度；</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">5<font face=\"宋体\">国家重点纺织建设工程项目（包括技术改造项目）引进技术设备活动中的意向、对策和底数，对外询价、报价资料和对外商提供的图纸资料的审查结论。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">第四条纺织行业工作中涉及其他部门或行业的国家秘密事项，按国家有关主管部门的保密范围规定确定密级。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">第五条本规定由中国纺织总会解释。</span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">第六条本规定自<font face=\"Times New Roman\">1996</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">1</font><font face=\"宋体\">日起实施。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">纺织工业部、国家保密局<font face=\"Times New Roman\">1989</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">l0</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">24</font><font face=\"宋体\">日颁布的《纺织工业国家秘密及其密级具体范围的规定》</font><font face=\"Times New Roman\">([1989]</font><font face=\"宋体\">纺办第</font><font face=\"Times New Roman\">27</font><font face=\"宋体\">号）同时废止。</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p><p class=\"p0\" style=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt; TEXT-INDENT: 21.75pt\"><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;&nbsp;&mdash;&mdash;摘自<font face=\"Times New Roman\">1996</font><font face=\"宋体\">年</font><font face=\"Times New Roman\">4</font><font face=\"宋体\">月</font><font face=\"Times New Roman\">8</font><font face=\"宋体\">日中国纺织总会、国家保密局文件&nbsp;纺办</font><font face=\"Times New Roman\">[1996]13</font><font face=\"宋体\">号</font></span><span style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"><o:p></o:p></span></p></div><!--EndFragment-->', '中国纺织总会、国家保密局', '全国', '4', '7', '6', '1', '1996-04-08 00:00:00', null, 'ca82ca4a32fc15d50132fc2ade36000e', 'ca82ca4a32fc15d50132fc2ade26000d', 'ca82ca4a32fc15d50132fc3293f70010', '2011-10-25 00:00:00', null, null, null, null, '0');
