INSERT INTO `sys_config_text_info` VALUES ('agenda', '我的日程', '【我的日程】为您提供新增、编辑、删除个人工作日程，并提供日历、列表两种显示模式。', '我的日程', '我的日程', '1', '2013-01-13 16:36:31', '1', '1', '2013-01-13 16:36:31', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('agenda_calender', '日程安排', '【日程安排】可以帮助您查询搜索个人工作日程安排，默认日历模式让您更加快捷、直观的查看个人日程。', '日程安排', '日程安排', '1', '2013-01-13 19:36:31', '1', '1', '2013-01-13 19:53:05', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_caseCriticalviolation', '严重违规案件', '【严重违规案件】 如：将涉密计算机、涉密存储设备接入互联网及其他公共信息网络，将导致涉密信息处于不可控状态，直接危害国家秘密安全的事件。', '严重违规案件', '严重违规案件', '1', '2013-08-14 11:02:48', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_discloseSecrecy', '泄密案件', '【泄密案件】 是指违反保密法律法规，使国家秘密被不应知悉者知悉，或者超出限定的接触范围，而不能证明未被不应知悉者知悉的事件。', '泄密案件', '泄密案件', '1', '2013-08-14 10:56:36', '1', '1', '2013-08-20 15:07:46', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_key_department', '保密要害部门', '【保密要害部门】是指机关、单位日常工作中产生、传递、使用和管理绝密或较多机密级、秘密级国家秘密的内设机构。<br />在这里您可以填写单位的要害部门情况，并可以填写要害部门的下属涉密部位和涉密人员。', '保密要害部门', '保密要害部门', '1', '2012-12-03 16:41:13', '1', '1', '2012-12-04 10:16:48', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_key_part', '保密要害部位', '【保密要害部位】是指机关、单位内部集中制作、存储、保管涉密载体的专门场所，如机关、单位的保密室、档案室和涉密信息系统机房等。<br />在这里您可以填写单位的要害部位情况，并可以填写要害部位的涉密人员。', '保密要害部位', '保密要害部位', '1', '2012-12-04 10:17:28', '1', '1', '2012-12-04 10:20:45', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_lawsystem_secrecy_law_code', '法律法规', '【法律法规】可以帮助保密行政管理部门对辖区内保密制度的制定、发布提供统一管理。', '法律法规', '法律法规', '1', '2012-12-04 10:36:31', '1', '1', '2012-12-04 10:53:05', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_lawsystem_secrecy_scope', '保密范围', '【保密范围】可以帮助保密行政管理部门对辖区内保密范围制定提供统一管理。', '保密范围', '保密范围', '1', '2012-12-04 10:36:31', '1', '1', '2012-12-04 10:53:05', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_computer', '涉密计算机', '【涉密计算机】 存储、处理涉及国家秘密信息的计算机。', '涉密计算机', '涉密计算机', '1', '2013-08-14 10:21:45', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_countryitem', '国家秘密事项', '【国家秘密】 国家秘密是关系国家安全和利益，依照法定程序确定，在一定时间内只限一定范围的人员知悉的事项。', '国家秘密事项', '国家秘密事项', '1', '2013-08-14 09:55:36', '1', '1', '2013-08-14 09:55:55', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_mobilestoragemedia', '涉密移动存储介质', '【涉密移动存储介质】 存储涉及国家秘密信息的便携数据存储载体。', '涉密移动存储介质', '涉密移动存储介质', '1', '2013-08-14 10:24:12', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_network', '涉密网络', '【涉密网络】 由计算机及其相关配套设备、设施构成的，按照一定的应用目标和规则存储、处理、传输涉密信息的系统或者网络。', '涉密网络', '涉密网络', '1', '2013-08-14 10:28:59', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_networkterminal', '涉密网络终端', '【涉密网络终端】 连接涉密网络的计算机终端。', '涉密网络终端', '涉密网络终端', '1', '2013-08-14 10:30:11', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_organ', '保密组织机构', '【保密组织机构】是指按照保密相关要求，中央国家机关、县级以上国家机关和涉及国家秘密的单位（如高校、大型企业单位、大型事业单位），设立保密工作管理机构（通常称保密委员会或保密工作领导小组）或指定某个部门承办日常保密管理工作。', '保密组织机构', '保密组织机构', '1', '2012-12-04 11:44:13', '1', '1', '2012-12-04 11:47:34', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_organ_secrecy_committee', '保密委员会', '        各级党委保密委员会办公室和各级保密行政管理部门是一个机构，两块牌子。党的中央委员会设立保密委员会，是党中央统一领导党政军保密工作的领导机构；中央保密委员会下设办公室，与国家保密局“一个机构，两块牌子”，既是中央保密委员会的日常办事机构，也是主管全国保密工作的国务院职能部门。县级以上地方各级党委设立保密委员会，是党管保密的专门组织，下设保密委员会办公室，与同级保密局“一个机构，两块牌子”，既是保密委员会的办事机构，也是主管本行政区域保密工作的政府职能部门。', '保密委员会', '保密委员会', '1', '2012-12-03 16:41:13', '1', '1', '2013-01-06 11:34:22', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_organ_secrecy_office', '保密办（保密局）', '        【保密办（保密局）】是国家保密行政管理部门。国家保密局主管全国保密工作，主要职责有：贯彻党中央、国务院有关保密工作方针、政策、决定、指示，依法履行行政管理职责；制订全国保密工作计划并组织实施，提出改进和加强保密工作的全局性、政策性建议；拟订保密法规，经国家立法机关审定颁布，制定或会同中央有关部门制定保密规章制度，．负责保密法规的解释；依法组织开展保密宣传教育、保密检查、保密技术防护和泄密案件查处工作，对机关、单位的保密工作进行指导和监督；代表国家处理涉外有关保密工作事务等。“县级以上地方各级保密行政管理部门”指省级、市级、县级保密局，在上级保密行政管理部门指导下，依法指导、协调、监督本地区机关、单位的保密工作。', '保密办（保密局）', '保密办（保密局）', '1', '2012-12-03 16:41:13', '1', '1', '2012-12-04 10:16:48', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_others', '其他涉密设备', '【其他涉密设备】 处理涉密信息的打印机、传真机、复印机、扫描仪、照相机、摄像机、晒图机、一体机等具有信息存储和处理功能的设备。', '其他涉密设备', '其他涉密设备', '1', '2013-08-14 10:26:15', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_person', '涉密人员', '【涉密人员】是指在涉密岗位工作的人员，而涉密岗位是指在日常工作中产生、经管或者经常接触、知悉国家秘密事项的岗位。<br />涉密人员按照涉密程度分为核心涉密人员、重要涉密人员、一般涉密人员，并实行分类管理。', '涉密人员', '涉密人员', '1', '2012-12-04 10:36:31', '1', '1', '2012-12-04 10:53:05', '1', '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_products', '密品', '【密品】 密品是指直接含有国家秘密信息，或者通过观察、测试、分析等手段能够获所承载的国家秘密信息的设备和产品。如有关密码设备，需要保密的新型尖端武器装备，具有国际领先技术水平并且需要保密的科学技术设备、产品，通过特殊途径引进的且根据约定需要对外承担保密义务的产品等。', '密品', '密品', '1', '2013-08-14 10:15:02', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('bm_secrecy_researchproject', '涉密科研项目', '【涉密科研项目】 保密普查的涉密科研项目，指军工单位、科研院所、高等院校等单位在填报年度内申报并经批准的涉密科研项目。', '涉密科研项目', '涉密科研项目', '1', '2013-08-14 10:16:14', '1', null, null, null, '1');
