INSERT INTO `sys_config_text_info` VALUES ('mail_accept', '收件箱', '收件箱其主要作用是收取来自其他人的e-mail，您可以在此查看新的电子邮件，该模块下提供A.查询邮件 B.查看新邮件 C.删除已读或垃圾邮件的功能。', '收件箱', '收件箱', '1', '2013-01-17 17:27:39', '1', null, null, null, '1');
