INSERT INTO `sys_config_text_info` VALUES ('mail_recycle', '回收站', '回收站主要用来存放用户临时删除的文档资料，用好和管理好回收站、打造富有个性功能的回收站可以更加方便日常的文档维护工作，功能包括：A.查询邮件 B.还原已删除邮件 C.底删除邮件(该功能不可逆，请谨慎操作)。', '回收站', '回收站', '1', '2013-01-17 17:35:17', '1', null, null, null, '1');
INSERT INTO `sys_config_text_info` VALUES ('mail_send', '发件箱', '发件箱为您提供保存已发送邮件的详细信息,在这里你可以执行查询、转发、重发、删除、查看附件和查看回执等操作', '发件箱', '发件箱', '1', '2013-01-17 17:30:05', '1', null, null, null, '1');
