INSERT INTO `sys_config_text_info` VALUES ('sys_ukey_manage_pinless', 'UKey管理-绑定', '绑定操作是对指定人员与插入的设备进行绑定操作。绑定时将擦除原uKey信息请谨慎操作：该情况下建议先进行识别操作。误操作后你可以使用系统的<font color=\"blue\">更换</font>功能找回原uKey信息。', 'Ukey管理-绑定', 'UKey管理-绑定', '1', '2013-03-28 09:24:42', '1', null, null, null, '1');
